import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { ReactDOM } from 'react';
import { Container } from 'reactstrap';

var Courses = [
  {
    courseId: 0,
    date: '01-02-22', 
    title: 'Get fit', 
    description: 'Dieting, excercise and other important things', 
    teacher: 'Chodakowska' 
  },
  {
    courseId: 1,
    date: '02-02-22', 
    title: 'Get fit 2', 
    description: 'Dieting, excercise and other important things', 
    teacher: 'Chodakowska' 
  },
];

export class UserCourses extends Component {
  static displayName = UserCourses.name;

  constructor(props) {
    super(props);    
    this.state = { 
      courses: Courses
    };
  }


//TODO do naprawy Daga
  // renderTableBody() {
  //   let data = [];
  //   for (let i = 0; i < this.courses.length; i++) {
  //     for (let j = 0; j < this.courses[i].length; j++) {
  //       data.push(<td>{data[i][j]}</td>);
  //     }
  //   }
  //   return data;
  // }

  render() {

    const CoursesTable = this.state.courses.map((course) => {
      return (
        <tr key={course.courseId}>
          <td>{course.date}</td>
          <td>{course.title}</td>
          <td>{course.description}</td>
          <td>{course.teacher}</td>
        </tr>
      )
    })

    let Table;
    if(this.state.courses.length > 0) {
      Table  = 
      <table class="table table-hover">
      <thead>
      
        <tr>
          <th scope="col">Date time</th>
          <th scope="col">Course</th>
          <th scope="col">Description</th>
          <th scope="col">Teacher</th>
        </tr>
      </thead>
      <tbody ref={this.tableBody}>         
        { CoursesTable }
      </tbody>
    </table>
    }
   
    return (    
      <div>
        <div className="d-flex justify-content-between mb-3">
          <h1>Your courses:</h1>  
          <Link className="btn btn-primary m-2" to="/all-courses">See all our courses</Link>
                    
        </div>
      
      { Table }

      </div>
    
    );
  }
}
