import React, { Component } from 'react';
import { Link } from 'react-router-dom';

var allCourses = [
  {
    courseId: 0,
    date: '01-02-22', 
    title: 'Get fit', 
    description: 'Dieting, excercise and other important things', 
    teacher: 'Chodakowska' 
  },
  {
    courseId: 1,
    date: '02-02-22', 
    title: 'Get fit 2', 
    description: 'Dieting, excercise and other important things', 
    teacher: 'Chodakowska' 
  },
  {
    courseId: 2,
    date: '02-05-22', 
    title: 'Learn maths', 
    description: '2+2=4', 
    teacher: 'Pitagoras' 
  },
  {
    courseId: 3,
    date: '14-10-22', 
    title: 'Learn music', 
    description: 'la la al', 
    teacher: 'Bethoven' 
  },
];


export class AllCourses extends Component {
  static displayName = AllCourses.name;

  constructor(props) {
    super(props);
    this.state = { 
      courses: allCourses
    };
  }

 

  render() {
     const CoursesTable = this.state.courses.map((course) => {
      return (
        <tr key={course.courseId}>
          <td>{course.date}</td>
          <td>{course.title}</td>
          <td>{course.description}</td>
          <td>{course.teacher}</td>
        </tr>
      )
    })

    let Table;
    if(this.state.courses.length > 0) {
      Table  = 
      <table class="table table-hover">
      <thead>
      
        <tr>
          <th scope="col">Date time</th>
          <th scope="col">Course</th>
          <th scope="col">Description</th>
          <th scope="col">Teacher</th>
        </tr>
      </thead>
      <tbody ref={this.tableBody}>         
        { CoursesTable }
      </tbody>
    </table>
    }
   
    return (    
      <div>
        <div className="d-flex justify-content-between mb-3">
          <h1>Your courses:</h1>  
          <Link className="btn btn-primary m-2" to="/user-courses">Go back to my courses</Link>
                    
        </div>
      
      { Table }

      </div>
    
    );
  }
}
